import { get } from "../../services/services";
import { mapGetters } from "vuex";
import Exporter from "vue-chartjs-exporter";
// import html2canvas from "html2canvas";
// import jsPDF from "jspdf";
import "jspdf-autotable";
import {
  Dropdown,
  DropdownItem,
  DropdownMenu,
  Table,
  TableColumn,
  Button,
  Dialog,
  MessageBox,
  Row,
  Col,
  Form,
  FormItem,
  Select,
  Option,
  TimePicker,
  DatePicker,
  CheckboxButton,
  Checkbox,
  CheckboxGroup,
  Switch,
  Radio,
  Tag,
  RadioGroup,
} from "element-ui";
import Vue from "vue";
import { Pie } from "vue-chartjs";
export default {
  components: {
    Pie,
    name:'app',
    LineChart,
    BarChart,
    LineChartNew,
    BaseProgress,
    StatsCard,
    [Dropdown.name]: Dropdown,
    [DropdownItem.name]: DropdownItem,
    [DropdownMenu.name]: DropdownMenu,
    [Button.name]: Button,
    [Dialog.name]: Dialog,
    [Tag.name]: Tag,
    [Row.name]: Row,
