// Charts
import * as chartConfigs from "@/components/Charts/config";
import LineChart from "@/components/Charts/LineChart";
import BarChart from "@/components/Charts/BarChart";
import LineChartNew from "@/components/Charts/LineChartNew";
// Components
import BaseProgress from "@/components/BaseProgress";
import StatsCard from "@/components/Cards/StatsCard";

// Tables
// import SocialTrafficTable from './Dashboard/SocialTrafficTable';
// import PageVisitsTable from './Dashboard/PageVisitsTable';
import { get } from "../../../services/services";
import { mapGetters } from "vuex";
import Exporter from "vue-chartjs-exporter";
// import html2canvas from "html2canvas";
// import jsPDF from "jspdf";
import "jspdf-autotable";
import {
  Dropdown,
  DropdownItem,
  DropdownMenu,
  Table,
  TableColumn,
  Button,
  Dialog,
  MessageBox,
  Row,
  Col,
  Form,
  FormItem,
  Select,
  Option,
  TimePicker,
  DatePicker,
  CheckboxButton,
  Checkbox,
  CheckboxGroup,
  Switch,
  Radio,
  Tag,
  RadioGroup,
} from "element-ui";
import Vue from "vue";
import { Pie } from "vue-chartjs";
export default {
  components: {
    Pie,
    name:'app',
    LineChart,
    BarChart,
    LineChartNew,
    BaseProgress,
    StatsCard,
    [Dropdown.name]: Dropdown,
    [DropdownItem.name]: DropdownItem,
    [DropdownMenu.name]: DropdownMenu,
    [Button.name]: Button,
    [Dialog.name]: Dialog,
    [Tag.name]: Tag,
    [Row.name]: Row,
    [Col.name]: Col,
    [Form.name]: Form,
    [FormItem.name]: FormItem,
    [Select.name]: Select,
    [Option.name]: Option,
    [TimePicker.name]: TimePicker,
    [DatePicker.name]: DatePicker,
    [CheckboxButton.name]: CheckboxButton,
    [CheckboxGroup.name]: CheckboxGroup,
    [Switch.name]: Switch,
    [Checkbox.name]: Checkbox,
    [Radio.name]: Radio,
    [CheckboxGroup.name]: CheckboxGroup,
    [RadioGroup.name]: RadioGroup,
    [Table.name]: Table,
    [TableColumn.name]: TableColumn,

  },
  computed: {
    ...mapGetters({
      // map `this.doneCount` to `this.$store.getters.doneTodosCount`
      user: "user",
    }),
    main_1() {
      return this.status ? "mb-5 mb-xl-0 mt--5" : "mb-5 mb-xl-0";
    },
    main_2() {
      return this.status ? "information none" : "information";
    },
    main_3() {
      return this.status ? "" : "none";
    },
    notBack() {
      return this.status2 ? "mt-4" : "mt-4 none";
    },
    back() {
      return this.status2 ? "none" : "mt-4";
    },
    print() {
      return this.status ? " mb-7 mb-xl-4 none" : "mb-7 mb-xl-4";
    },
  },
  data() {
    return {
      post: [],
      renderChart: {
        chartData: {
          type: Pie,
          default: null
        },
        extraOptions: chartConfigs.blueChartOptions,
      },
      download_status: "Download Report",
      status: true,
      status2: true,
      chart: [],
      options: {

      },
    };
  },

  watch: {
    value(after, before) {
    },
    "$route.params.id": function (id) {
      this.fetchData();
    },
    data: function(){
      this.renderChart();
    }
  },
  methods: {
    fetchData() {
      let url = "http://localhost:8000/api/teacher/class/report/"+this.$route.params.id;
      get(url)
        .then((res) => {
          this.post = res.data;
          console.log(this.post)
          let very_good = parseFloat(this.post.very_good/this.post.total)*100
          let good = parseFloat(this.post.good/this.post.total)*100
          let average = parseFloat(this.post.average/this.post.total)*100
          let lower_avg = parseFloat(this.post.lower_average/this.post.total)*100
          let chartData = 
            {
              labels: ["Very Good", "Good", "Average", "Lower Average", "Fail"],

              datasets: [
                {

                  backgroundColor: ["#d48a8a", "#b5cf60", "#ffd360","#e66b60", "#296b60"],
                  data: [very_good, good, average, lower_avg, 0]
                }
              ],
            responsive: true, 
            maintainAspectRatio: false,
           }
          this.renderChart.chartData = chartData;


          // );
        })
        .catch((err) => {
          alert(err);
        });
    },
  },
  mounted() {
    this.fetchData();
    const canvas = this.$refs.canvas;
  },
};
